      color: #fff;
      scroll-behavior: smooth;
    }

    /* ====== HEADER ====== */
    header {
      background-color: #000;
      color: #facc15;
      padding: 15px 60px;
      display: flex;
      justify-content: space-between;
      align-items: center;
      box-shadow: 0 4px 10px rgba(0,0,0,0.4);
      position: sticky;
      top: 0;
      z-index: 10;
    }

    header h1 {
      font-size: 24px;
      letter-spacing: 1px;
    }

    nav ul {
      list-style: none;
      display: flex;
      gap: 25px;
    }

    nav ul li a {
      color: #fff;
      text-decoration: none;
      font-weight: 600;
      transition: 0.3s;
    }

    nav ul li a:hover {
      color: #facc15;
    }

    /* ====== HERO ====== */
    .hero {
      text-align: center;
      padding: 140px 20px;
      background: linear-gradient(to bottom, #111, #000);
    }

    .hero h2 {
      font-size: 48px;
      color: #fff;
      margin-bottom: 20px;
      font-weight: bold;
    }

    .hero p {
      font-size: 18px;
      color: #d1d5db;
      margin-bottom: 40px;
      max-width: 700px;
      margin-left: auto;
      margin-right: auto;
    }

    .hero a {
      display: inline-block;
      background-color: #facc15;
      color: #111;
      font-weight: bold;
      text-decoration: none;
      padding: 14px 36px;
      border-radius: 30px;
      transition: 0.3s;
    }

    .hero a:hover {
      background-color: #eab308;
    }

    /* ====== CATÁLOGOS ====== */
    #catalogos {
      padding: 100px 20px;
      background-color: #111;
      text-align: center;
    }

    #catalogos h2 {
      color: #facc15;
      font-size: 36px;
      margin-bottom: 40px;
    }

    .motos-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
      gap: 30px;
      max-width: 1200px;
      margin: 0 auto;
    }

    .moto-card {
      background-color: #1a1a1a;
      border-radius: 12px;
      padding: 20px;
      box-shadow: 0 4px 10px rgba(0,0,0,0.4);
      transition: 0.3s;
    }

    .moto-card:hover {
      transform: translateY(-5px);
      box-shadow: 0 8px 16px rgba(250, 204, 21, 0.3);
    }

    .moto-card img {
      width: 100%;
      height: 160px;
      object-fit: cover;
      border-radius: 10px;
      margin-bottom: 15px;
    }

    .moto-card h3 {
      color: #facc15;
      margin-bottom: 10px;
    }

    .moto-card p {
      color: #d1d5db;
      font-size: 15px;
      margin-bottom: 10px;
    }

    /* ====== FOOTER ====== */
    footer {
      background-color: #000;
      color: #888;
      text-align: center;
      padding: 15px 0;
      font-size: 15px;
    }

    footer span {
      color: #facc15;
      font-weight: bold;
    }
  </style>
